settings.liquid:
{% if customer %}
{% assign lowercase_tags = customer.tags | join: ',' | downcase | split: ',' %}
{% if lowercase_tags contains 'freebie-offer' %}
<style>
	.shopify-section--custom-liquid .section {
		margin: 0px;
	}

	.shopify-section--custom-liquid.container,
	.shopify-section--custom-liquid .container {
		padding: 0px;
	}

	.landing_page_section {
		background: #000;
	}

	.landing_page_section .desktop_image {
		width: 100%;
	}

	.landing_page_section .img_wrapper {
		position: relative;
	}

	.landing_page_section .img_wrapper .desktop_image {
		margin-top: -8px;
	}

	.section_7 {
		background: #000;
		margin-top: -8px;
	}
</style>

<div class="section_1 landing_page_section">
	<img src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/Free-B100_01.jpg?v=1662025145"
		class="desktop_image">
</div>
<div class="section_2 landing_page_section">
	<div class="desktop_image">
	</div>
</div>
<div class="section_3 landing_page_section">
	<div class="img_wrapper">
		<a href="https://www.boat-lifestyle.com/account/login?source=freebie"><img
				src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/Free-B100_02.jpg?v=1662025143"
				class="desktop_image"></a>
	</div>
</div>
<div class="section_4 landing_page_section">
	<div class="img_wrapper">
		<a href="#shopify-section-template--14621608640610__1662013282a3b758c0"><img
				src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/Free-B100_03.jpg?v=1662025143"
				class="desktop_image"></a>
	</div>
</div>
<div class="section_5 landing_page_section">
	<div class="img_wrapper">
		<img src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/Free-B100_04.jpg?v=1662025143"
			class="desktop_image">
	</div>
</div>
<div class="section_7 landing_page_section">
	<div class="img_wrapper">
		<a href="https://www.boat-lifestyle.com/account/login?source=freebie"><img
				src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/db_007.jpg?v=1661947703"
				class="desktop_image"></a>
	</div>
</div>
<div class="section_8 landing_page_section">
	<div class="img_wrapper">
		<img src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/db_008.jpg?v=1661947703" class="desktop_image">
	</div>
</div>
<script>
$(document).ready(function(){
	$(".shopify-section.shopify-section--featured-collections").css("display", "block");
});
</script>
{% else %}
<div class="featured-collections-container featured-collections-container-{{section.id}}">
	<div class="loading_icon">
		<img class="lazyload"
			src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/preloader_50x.gif?v=1649585765" alt="Load More"
			style="width: 50px;" />
	</div>
</div>
<script>
$(document).ready(function(){	window.location.href = "https://www.boat-lifestyle.com/account";
})
</script>
{% endif %}

{% endif %}
