{% if product.tags contains 'ring' %}
  <style>
    #custom-Modal.show .popover__overlay {visibility: visible;opacity: .3; top: 0;z-index:1111;}
    #custom-Modal{display: block;visibility: hidden;z-index:1000;padding:0!important;transform:translateY(100%);transition: transform 1s cubic-bezier(.75,0,.175,1),visibility .6s cubic-bezier(.75,0,.175,1);}
    #custom-Modal .close{position: absolute;top: 12px;right: 12px;width: 30px;height: 30px;line-height: 24px;opacity: 1;
      text-align: center;font-weight: 400!important;font-size: 30px!important;background: #d9d9d9;padding: 5px;border-radius: 50%!important;
    }
    div#custom-Modal.show {transform: translateY(0);visibility: visible;}
    #custom-Modal .modal-dialog {position: absolute!important;
      bottom: 0!important;
      top: unset!important;
      left: unset!important;
      transform: translate(0)!important;
      z-index: 9999;
    }
  </style>
  <div id="custom-Modal" class=" modal fade" role="dialog">
    <span class="popover__overlay"></span>
    <div class="modal-dialog w-100 m-0">
      <div class="modal-content w-100" style="border-radius: 16px 16px 0 0;">
        <div class="modal-body p-0">
          <button type="button" class="close" data-dismiss="modal">&times;</button>
          <div class="modal-body-content">
            <div class="p-4">         
                <div class="pt-5 mb-0 text-center" style="font-size: 24px;line-height: 1.2;">
                  We recommend downloading <b>Ring Sizer</b> mobile app & checking your ring size
                </div>
<div class="d-flex w-100 justify-content-evenly">
                <a href="https://apps.apple.com/app/ring-sizer-utility/id795721582"><img src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/appstore.png?v=1693471503" width="135" height="40" alt="Available on the AppStore"></a>
                <a href="https://play.google.com/store/apps/details?id=ru.cherrydesign.ringsizer"><img src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/googleplay.png?v=1693471505" width="129" height="45" alt="Get it on Google Play"></a>  </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
<script>
    $(document).ready(function() {
        $("#my-ring").click(function(e) {
      e.preventDefault();    console.log("clicked");
          $("#custom-Modal").modal("show");
        });
        $("#custom-Modal .close, #custom-Modal .popover__overlay").click(function(){
          $("#custom-Modal").removeClass("show");
        })
    });
</script>
{% endif %}
